INSERT INTO English VALUES('Psalms',23,3,'Yahweh restores my soul. He leads me on paths of righteousness for his name.');
INSERT INTO English VALUES('Psalms',23,4,'Although I walk through the valley of the shadow of death, I won''t be afraid of evil things because you''re with me. Your stick and your staff comfort me.');
INSERT INTO English VALUES('Psalms',23,5,'You prepare a table for me in front of my enemies. You anoint my head with oil. My cup overflows.');
